
-- Show calls, where prev call duration < 5 and current duration > 30
SELECT idcall, ks, dt, dur 
FROM 
(
	SELECT *, LAG(dur, 1) OVER ( PARTITION BY ks ORDER BY dt) AS prev_call_dur
	FROM f
) as calls
WHERE dur > 30 AND prev_call_dur < 5;

